import { createContext, useState, useEffect } from 'react';

import { addCollectionAndDocuments } from '../utilities/firebase/firebase';
import SHOP_DATA from '../shop-data'; //json里是一个array,这里assign a variable name to use

//create new context
export const ProductsContext = createContext({
    products: [],
    setProducts: () => null,
})

//context provider
export const ProductsProvider = (props) => {
    //useState to assign json value to context
    const [products, setProducts] = useState([]);
    useEffect(() => {
        addCollectionAndDocuments('categories', SHOP_DATA);
    }, []);

    const value = { products, setProducts }; //state和setState必须成对使用？

    return <ProductsContext.Provider value={value}>{props.children}</ProductsContext.Provider>
}
