import { createContext, useState } from "react";

//create a context variable - store value, set an intial value（这里是一个uesState的variable和setter function
export const UserContext = createContext({
    currentUser: null,
    setCurrentUser: () => null,
});


//SomeContext.provider - context provider is the actual component to use inside index.js, wrap components that need to use the value of the context provider
export const UserProvider = ({ children }) => {
    const [currentUser, setCurrentUser] = useState(null);
    const value = { currentUser, setCurrentUser }; 

    return <UserContext.Provider value={value}>{children}</UserContext.Provider>
}
