import { useContext } from 'react';
import { CartContext } from '../../contexts/cart.context';

import './checkout-item.styles.scss'



const CheckOutItem = ({item}) => {

    const { removeCheckoutItem, addItemToCart } = useContext(CartContext);


    return (
        <div className='checkout-item-container'>
            <div className='image-container'>
                <img src={item.imageUrl} alt={`${item.name}`} />
            </div>
                <span className='name'>{item.name}</span>
            <div className='quantity'>
                <span className='arrow' onClick={() => removeCheckoutItem(item)}> ‹ </span>
                <span className='value'>{item.quantity}</span>
                <span className='arrow' onClick={() => addItemToCart(item)}> › </span>
            </div>

                <span className='price'>{item.price}</span>
                <button className='remove-botton'> × </button>
        </div>
    );
};

export default CheckOutItem;
