import { useContext } from 'react';
import { CartContext } from '../../contexts/cart.context';
import  './cart-item.styles.scss';


const CartItem = () => {

    const { cartItems } = useContext(CartContext);

    return(
    cartItems.map((item) => {
        <div>
            <h2>{item.name}</h2>
            <span>{item.quantity}</span>
        </div>
    }));
};

export default CartItem;
